style.css:
body {background:black;
    color:white;
    font-family: Algerian;
}

h1{
    color:white;
    font-weight:bold;
    text-align:center;
    background:#000;
    text-decoration:underline;
    text-decoration-color:#f10;
    text-transform:capitalize;
    text-shadow:5px 0px 5px #0fe
}   

P:hover{
    text-decoration:underline;
    text-decoration-color:#040BF9;
    background:#000;
    font-family: Times New Roman;
    color:white;
    text-shadow:1px 0px 1px #0fe;
    text-transform:capitalize
}


P{
    color:#fff;
    font-family: Times New Roman;
    font-size:110%;
    font-weight:normal;
    text-align:left;
    line-height:25px;
    text-decoration:none;
    text-transform:lowercase;
}

a{
    font-size:110%;
    text-decoration:underline;
    margin-bottom:10px;
}

a:linck{
    color:#000;
    
}

a:visited{
    color:#f10;
    text-decoration:line-through;
}

a:hover{
    text-decoration:underline;
    text-decoration-color:#040BF9;
    background:#fff;
    font-family: Times New Roman;
    color:#000;
    text-shadow:1px 0px 1px #0fe;
    
}

a:active{
    background:#0fe;
    font-family: Arial;
}
