style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com"){

ytd-browse[page-subtype="home"] #grid-header.ytd-rich-grid-renderer{
    width: -webkit-fill-available !important; /* yoku wakattenai */
}
ytd-browse[page-subtype="home"] #contents.ytd-rich-grid-renderer{
    margin-right: initial !important;
    margin-left: initial !important;
    width: initial !important;
}

ytd-browse[page-subtype="home"] ytd-rich-section-renderer{
    margin-right: initial !important;
    margin-left: initial !important;
}
ytd-browse[page-subtype="home"] #rich-shelf-header.ytd-rich-shelf-renderer{
    margin: 18px 0px 18px 8px  !important;
}
ytd-browse[page-subtype="home"] #contents.ytd-rich-shelf-renderer{
    margin-right: initial !important;
    margin-left: initial !important;
}

/* split (channel icon - title) */
ytd-browse[page-subtype="home"] #avatar-link.ytd-rich-grid-video-renderer {
    margin-right: 6px !important;
}/* kyuu shiyou kamo */
ytd-browse[page-subtype="home"] #avatar-link.ytd-rich-grid-media {
    margin-right: 6px !important;
}

