options.value:
/* thumbnail size 180 split 4 */

ytd-browse[page-subtype="home"] ytd-rich-grid-renderer{
    margin-right: 16px !important;
    margin-left: 20px !important;
}

ytd-browse[page-subtype="home"] ytd-rich-item-renderer:not([is-movie]):not([is-post]) {
    margin-right: 4px !important; /* split size */
    margin-left: 0px !important;
    width: calc(100% / var(--ytd-rich-grid-items-per-row) - 4px - 0.01px)  !important; /* 4px = split size */
}

ytd-browse[page-subtype="home"] #contents.ytd-rich-grid-renderer, ytd-browse[page-subtype="home"] #grid-header.ytd-rich-grid-renderer{
     max-width: calc(var(--ytd-rich-grid-items-per-row) * 180px + var(--ytd-rich-grid-items-per-row) * 4px) !important;
}   /* 190px = max saize , 4px = split size */

ytd-browse[page-subtype="home"] #meta ytd-video-meta-block {
    min-width: 135px !important;
}

@media screen and (max-width: 859px){
    ytd-app[mini-guide-visible_] ytd-browse[page-subtype="home"] ytd-rich-grid-renderer {
        --ytd-rich-grid-items-per-row: 3 !important;
