style.css:
@-moz-document url-prefix("https://beta.protonmail.com/") {
/* 
- Set font-family to Open Sans if it is installed on machine, if not - use ugly Lucida Grande
- make text slimmer
- set font size to human-readable
*/
body {
	font-family: 'Open Sans', "Lucida Grande", Verdana, Arial, sans-serif;
    font-weight: 300;
    font-size: 120%;
}

/* reset bolds to 600 */
.bold, .strong, b, strong {
	font-weight: 600;
}

/* shrink sidebar */
.sidebar {
	width: var(--width-sidebar, 10%);
}

/* shrink conversations list */
.items-column-list {
	width: var(--width-conversation-column, 30%);
}

/* make New Emails counter tooltip's background dimmer, no need in screaming bright color, simple clean indicator is enough */
.navigation__counterItem {
	background: #657ee499; 
}


/* paint Trash and Delete Permanently icons red */
#shape-trash, #shape-delete {
	fill: #d94949ab;
}

/* Empty Folder icon(brush) to Bright Red */
#shape-empty-folder {
	fill: red;
}

/* ...and make a little red glow around it */
[data-test-id="toolbar:emptyfolder"] svg {
	filter: drop-shadow( 0px 0px 3px rgba(255, 0, 0, 0.8));
}


/* Inbox icon blue */
#shape-inbox {
	fill: #369bf4ab;
}

/* Starred icon yellow */
#shape-star {
	fill: rgb(234, 200, 25);
}

/* Archive icon green */
#shape-archive {
	fill: #85e685ab;
}

/* Spam icon orange */
#shape-spam {
	fill: #ff7800;
}

/* All Mail icon white */
#shape-all-emails {
	fill: #fff;
}


/* calm down screaming Compose button a little */
[data-test-id="sidebar:compose"] {
	color: #ffffffcc !important;
	background-color: #657ee433 !important;
	border: 1px solid #657ee455 !important;
}
}
