style.css:
@-moz-document url-prefix("https://beta.protonmail.com/") {
/* 
- Set faont-family to Open Sans if it is installed on machine, if not - use ugly Lucida Grande
- make text slimmer
- set font size to human-readable
*/
body {
	font-family: 'Open Sans', "Lucida Grande", Verdana, Arial, sans-serif;
    font-weight: 300;
    font-size: 120%;
}

/* reset bolds from 900 to 600 */
.bold, .strong, b, strong {
	font-weight: 600;
}

/* shrink sidebar */
.sidebar {
	max-width: 20rem !important;
}

/* shrink emails list */
.items-column-list {
	width: calc((65% + var(--width-sidebar, 19rem))*0.35);
}

/* New Emails counter tooltip */
.navigation__counterItem {
	background: #657ee499; 
}


/* paint Trash and Delete Permanently icons red */
[data-test-id='toolbar:movetotrash'] svg, [data-test-id='toolbar:deletepermanently'] svg, [title='Trash'] svg.navigation__icon {
	fill: #d94949ab !important;
}

/* Inbox icon blue */
[data-test-id='toolbar:movetoinbox'] svg, [title='Inbox'] svg.navigation__icon {
	fill: #369bf4ab !important;
}

/* Starred icon yellow */
[title='Starred'] svg.navigation__icon {
	fill: rgb(234, 200, 25) !important;
}

/* Archive icon green */
[data-test-id='toolbar:movetoarchive'] svg, [title='Archive'] svg.navigation__icon {
	fill: #85e685ab !important;
}

/* Spam icon orange */
[data-test-id='toolbar:movetospam'] svg, [title='Spam'] svg.navigation__icon {
	fill: #ff7800 !important;
}
}
