style.css:
@-moz-document url-prefix("https://beta.protonmail.com/") {
/* 
- Set faont-family to Open Sans if it is installed on machine, if not - use ugly Lucida Grande
- make text slimmer
- set font size to human-readable
*/
body {
	font-family: 'Open Sans', "Lucida Grande", Verdana, Arial, sans-serif;
    font-weight: 300;
    font-size: 120%;
}

/* reset bolds from 900 to 600 */
.bold, .strong, b, strong {
	font-weight: 600;
}

/* shrink sidebar */
.sidebar {
	width: var(--width-sidebar, 10%);
}

/* shrink conversations list */
.items-column-list {
	width: var(--width-conversation-column, 30%);
}

/* make New Emails counter tooltip background dimmer, no need in screaming bright color, indicator is enough */
.navigation__counterItem {
	background: #657ee499; 
}


/* paint Trash and Delete Permanently icons red */
#shape-trash, #shape-delete {
	fill: #d94949ab;
}

/* Empty Folder icon to Bright Red */
#shape-empty-folder {
	fill: red;
}

/* ...and make a little red glow around Empty Folder icon */
[data-test-id="toolbar:emptyfolder"] svg {
	filter: drop-shadow( 0px 0px 2px rgba(255, 0, 0, 0.8));
}


/* Inbox icon blue */
#shape-inbox {
	fill: #369bf4ab;
}

/* Starred icon yellow */
#shape-star {
	fill: rgb(234, 200, 25) !important;
}

/* Archive icon green */
#shape-archive {
	fill: #85e685ab !important;
}

/* Spam icon orange */
#shape-spam {
	fill: #ff7800 !important;
}
}
