style.css:
@-moz-document domain("avaloniaonline.com") {
.tooltip, .tooltip2 {
    text-shadow: -1px -1px 0 #f00, 1px -1px 0 #f00, -1px 2px 0 #f00, 1px 2px 0 #f00;
}
.windowborder {
    border: 32px solid transparent;
    text-shadow: -1px -1px 0 #f90000, 1px -1px 0 #f00, -1px 2px 0 #f00, 1px 2px 0 #f00;
    color: #ff8000;
}
.titletext {
    color: #00ebff;
    text-shadow: -1px -1px 0 #0002ff, 1px -1px 0 #0300ff, -1px 2px 0 #0e00ff, 1px 2px 0 #000cff;
}
.gamelink {
    color: #fff200;
}
.tooltip2:hover:after {
    background: rgba(0,0,0,.6);
    color: #ff8000;
}
.tooltip:hover:after {
    background: rgba(0,0,0,.6);
    color: #ff8000;
}
.uploadstitched {
    background: #00c000;
    color: #ff8000;
    border: 2px dashed #f2ff00;
    box-shadow: 0 0 0 4px #00c000, 2px 1px 6px 4px rgba(10, 10, 0, 0.5);
}
@keyframes rainbow {
    0% { background-color: #ff0000a1 }
	5% { background-color: #ff0000a1 }
	15% { background-color: #ff8800a1 }
	25% { background-color: #ffff00a1 }
    35% { background-color: #88ff00a1 }
	45% { background-color: #00ff88a1 }
	55% { background-color: #0088ffa1 }
	65% { background-color: #0000ffa1 }
	75% { background-color: #8800ffa1 }
	85% { background-color: #ff0088a1 }
	95% { background-color: #ff0000a1 }
	100% { background-color: #ff0000a1 }
}
div#newpopup.windowborder{
    animation: rainbow 1s infinite;
}
#coinscount {
    color: #0000;
    text-shadow: 2px 2px 0 #0000;
}
.storycanvas {
    border: 4px solid #ff8000;
    -webkit-mask-image: -webkit-radial-gradient(#fff, #000);
}.uploadstitched {
    background: #889588;
    color: #ff8000;
    border: 2px dashed #889588;
    box-shadow: 0 0 0 4px #717971, 2px 1px 6px 4px rgba(10, 10, 0, 0.5);
}
textarea {
    border-color: rgb(255, 1, 1);
}
.profilebutton {
    border: 3px solid #ff8000;
    background: rgba(0, 0, 0, .67);
}
@keyframes Coins {
    0% { color: #f00 }
	5% { color: #f00 }
	15% {color: #f80 }
	25% { color: #ff0 }
	35% { color: #8f0 }
	45% { color: #0f8 }
	55% { color: #08f }
	65% { color: #00f }
	75% { color: #80f }
	85% { color: #f08 }
	95% { color: #f00 }
	100% { color: #f00 }
}
span.count{
    animation: Coins 1s infinite;
    background-color: black; 
}
}
