style.css:
@-moz-document domain("www.twitch.tv") {
  
	@font-face {
          font-family: Roboto;
          src: url('https://fonts.googleapis.com/css?family=Roboto&display=swap');
          font-weight: 400;
  		  font-style: Roboto;
	}
	@keyframes slide-bck-center {
		from {
			opacity: 0;
			transform: translateZ(0);
		}
		to {
			will-change: transform;
			opacity: 1;
			transform: translateZ(-400px);
		}
	}

	@keyframes newMessage {
		from {
			opacity: 0;
			transform: translateX(50px);
		}
		to {
			will-change: transform;
			opacity: 1;
			transform: translateX(5px);
		}
	}
	.chat-line__message:not(.special-message) {
		color: inherit;
		position: relative;
		box-sizing: border-box;
		opacity: 1;
		overflow: hidden;
		padding: 15px !important;
		animation: newMessage 750ms forwards;
	}
	.chat-line__message:nth-child(2n):not(.special-message) {
		padding: 0 15px;
		background-color: rgba(0, 0, 0, .1) !important;
	}
	.chat-line__message:nth-child(2n + 1):not(.special-message) {
		padding: 0 15px;
		background-color: inherit !important;
	}
	.special-message {
		color: inherit;
		position: relative;
		box-sizing: border-box;
		padding: 15px !important;
		opacity: 1;
		overflow: hidden;
	}
	.chat-line__message-body--highlighted {
		color: white;
		position: relative;
		box-sizing: border-box;
		opacity: 1;
		animation: slide-bck-center;
	}
	.channel-points-reward-line {
		color: #7200ff;
	}
	.panel-description {
		position: relative;
		box-sizing: border-box;
		opacity: 1;
		overflow: hidden;
		padding: 1px !important;
		animation: slide-bck-center;
	}
	.tw-line-height-body {
		position: relative;
		box-sizing: border-box;
		opacity: 1;
		overflow: hidden;
		padding: 15px !important;
		animation: slide-bck-center;
	}

	.default-panel {
		width: 250px;
		box-shadow: 0 2px 4px 0 rgba(0, 0, 0, 0.2), 0 3px 10px 0 rgba(0, 0, 0, 0.19);
		text-align: center;
		position: relative;
		box-sizing: border-box;
		opacity: 1;
		overflow: hidden;
		padding: 15px !important;
		animation: slide-bck-center;
	}

	.slide-bck-center {
		animation: slide-bck-center 0.45s cubic-bezier(0.470, 0.000, 0.745, 0.715) both;
	}
	.simplebar-content {
		position: relative;
		box-sizing: border-box;
		opacity: 1;
		overflow: hidden;
		padding: 15px !important;
		animation: slide-bck-center;
	}
	.channel-root__right-column {
		text-align: center;
		position: relative;
		box-sizing: border-box;
		opacity: 1;
		overflow: hidden;
		animation: slide-bck-center;
	}
	.persistent-player {
		position: relative;
		box-sizing: border-box;
		opacity: 1;
		overflow: hidden;
		animation: slide-bck-center;
	}
	
  
#button
	.tw-toggle__button {
		display: block;
		width: 36px;
		margin: 0px;
		background: #A1A1A1;
		position: absolute;
		top: 0;
		bottom: 0;
		right: 24px;
		transition: all 0.3s ease-in 0s;
	}
	.tw-toggle__button:after {
		position: relative;
		width: 60px;
		-webkit-user-select: none;
		-moz-user-select: none;
		-ms-user-select: none;
	}
	.tw-toggle__button:before {
		display: block;
		float: left;
		width: 50%;
		height: 36px;
		padding: 0;
		line-height: 32px;
		font-size: 16px;
		color: white;
		font-family: Trebuchet, Arial, sans-serif;
		font-weight: bold;
		box-sizing: border-box;
		border: 2px solid transparent;
		background-clip: padding-box;
	}
