* {
    border: none !important;
    /*font-family: "Nunito", Times, serif;*/
}
/* Topbar */

#container.ytd-masthead {
    height: 35px;
}

#masthead-container.ytd-app {
    margin-top: -15px;
    margin-left: 5%;
    /*max-width: 1000px;*/
    width: 90%;
    opacity: 0;
    transition: opacity 0.5s, margin-top 0.5s, margin-left 0.5s, width 0.5s;
}

#masthead-container.ytd-app:hover {
    opacity: 1;
    margin-top: 10px;
    margin-left: 2.5%;
    width: 95%;
    /*max-width: 500px;*/
    transition: opacity 0.5s, margin-top 0.5s, margin-left 1s, width 1s;
}

#masthead-container.ytd-app:hover ~ #page-manager {
    filter: blur(5px);
    transition: filter 1s;
}

#page-manager {
    filter: blur(0px);
    transition: filter 1s;
}

#masthead {
    height: 35px;
    border-radius: 50px;
}
#search-icon-legacy.ytd-searchbox {
    border-radius: 30px;
    height: 25px;
    width: 25px;
    margin-left: -40px;
    margin-top: 2px;
}

#search-form.ytd-searchbox:hover > #search-icon-legacy.ytd-searchbox {
    opacity: 0;
}

#container.ytd-searchbox {
    border-radius: 30px;
    height: 25px;
    transition: background-color 1s, height 1s;
}

