/* ==UserStyle==
@name           harem-heroes full page
@namespace      DarkNio
@version        1.0.0
@description    harem-heroes
@author         DarkNio
==/UserStyle== */
@-moz-document url("https://www.nutaku.net/games/harem-heroes/play/") {
    .game-play-full .game-frame iframe {
        width: 100%;
        height: 100%;
        padding: 0;
        border: 0;
        position: fixed;
        top: 0px;
        z-index: 100;
        left: 0px;
    }

    html {
        overflow: hidden;
    }


    #hh_game {
        border: none;
        width: 100%;
        display: block;
        height: 640px !important;
    }
    img.hh-promo {
        height: 0px !important;
        width: 0px !important;
    }
}
