style.css:
@-moz-document domain("horaro.org") {
body {
    background-color: #333;
    color: white!important;
}

/* For "Currently Showing" and "Next Up" panels */
.panel-body {
    background-color: #757171;
    color: #fff;
    font-weight: bold;
}

.panel-success {
    background-color: #9d347a!important;
}

/* Table Entries */

.h-even td {
    background-color: #333;
}

.h-odd td {
    background-color: #4f4848;
}

/* Day Seperators */
.info td{
    background-color: #7bbde8!important;
    color: #2b2a29!important;
}

/* Current Run  */
.success td{
    background-color: #d6e9c6!important;
    color: #000!important;
    font-weight: bold;
}

/* Elements for non-schedule pages */
.jumbotron {
    background-color: #3c3a3a;
}

.well {
    background-color: #3c3a3a;
}
}
