/*___ Plain ________________________________________________________________________*/

#nav .nav-panel--dropdown,
.category-products-grid .item,
.col-main .owl-item,
.dropdown-menu,
.grid-view ul.products li.widget,
.list-view ul.products li,
.modal-content {
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

#nav .nav-panel--dropdown:hover,
.category-products-grid .item:hover,
.col-main .owl-item:hover,
.dropdown-menu:hover,
.grid-view ul.products li.widget:hover,
.list-view ul.products li:hover,
.modal-content:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Red Gradient _________________________________________________________________*/

.btn-danger,
.cc-color-override--928061755.cc-window {
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Blue Gradient _________________________________________________________________*/

ul.messages li.seller-message .message-body {
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Green Gradient ________________________________________________________________*/

ul.products li .image ul.badges li.featured {
	background: linear-gradient(to bottom, #1ec580 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Purple Gradient _______________________________________________________________*/

.nav-tabs>li.active>a,
.navbar-default .navbar-nav>.active>a,
.navbar-default .navbar-nav>.open>a,
.navbar-default .navbar-nav>.open>a:focus,
.navbar-default .navbar-nav>.open>a:hover,
.panel-login>.panel-heading-c .tab.active,
.scroll-to-top,
ul.messages li.customer-message .square,
ul.products li .image ul.badges li
{
	background: linear-gradient(to bottom, #2f3490 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Orange Gradient _______________________________________________________________*/

.note-msg,
.notice-msg,
.nav-regular .nav-item.level0.active > a,
.gen-tabs-style1 .tabs a.current,
ul.products li .image ul.badges li.one-cent-shipping {
	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*___ Highlight Background _________________________________________________________*/

#listings-filters .widget:hover,
.data-table tr.even:hover,
.data-table tr.odd:hover,
.dropdown-menu>li>a:focus,
.dropdown-menu>li>a:hover,
.list-view ul.products li:hover,
.product-details-attr .product-details-attr-label:hover,
.product-page ul.product-data li:hover,
.table-striped>tbody>tr:nth-of-type(even):hover,
.table-striped>tbody>tr:nth-of-type(odd):hover {
	background: linear-gradient(to right, #383838, #0000)!important;
	border-left-color: #434343!important;
}

.dropdown-menu>li:first-child>a:hover {
	border-top-left-radius: 22px;
}

.dropdown-menu>li:last-child>a:hover {
	border-bottom-left-radius: 22px;
}
}

@-moz-document domain("shopgoodwill.com"), domain("goodwillbooks.com") {
/*__________________________________________________________________________________*/

.category-products-grid .item,
.col-main .owl-item,
.data-table tr,
.grid-view ul.products li.widget,
.list-view ul.products li,
.mini-banners ul li,
.product-details-attr .product-details-attr-label,
.product-details-attr .product-details-attr-value,
.product-page ul.product-data li,
.product-thumbnails.owl-carousel .owl-item,
footer.site-footer .footer-lower .social li
{
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.category-products-grid .item:hover,
.col-main .owl-item:hover,
.data-table tr:hover,
.grid-view ul.products li.widget:hover,
.list-view ul.products li:hover,
.mini-banners ul li:hover,
.product-details-attr .product-details-attr-label:hover + .product-details-attr-value,
.product-details-attr .product-details-attr-label:hover,
.product-details-attr .product-details-attr-value:hover,
.product-page ul.product-data li:hover,
.product-thumbnails.owl-carousel .owl-item .carousel-thumb.selected,
.product-thumbnails.owl-carousel .owl-item:hover,
footer.site-footer .footer-lower .social li:hover
{
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

img {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/

p,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

p:hover,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

/*__________________________________________________________________________________*/

selector {
	opacity: .8!important;
}

table.dataTable tr ~ td.dataTables_empty, table.dataTable tr ~ th.dataTables_empty,
.grid-view ul.products li:hover .hover-actions button {
	opacity: 1!important;
}
}

