style.css:
@-moz-document url-prefix("https://e.mail.ru/"), url-prefix("https://mail.ru/") {
:root {
    --generalBlueBackgroundColor: rgba(0, 0, 30, 0.72);
    --brightBlueBackgroundColor: rgba(0, 0, 30, 0.3);
    --linkColor: #c6deff;
    --visitedLinkColor: #fff;
    --darkGreyColor: #111;
}




.llc__background {
    background-color: rgba(0, 0, 10, 0.7) !important; 
}

.llc:hover .llc__panel, .llc:hover .llc__background {
    background-color: rgba(30, 30, 30, 1) !important; 
}

.ll-sj__normal, .ll-sj__search, .ll-crpt, .ll-crpt-t {
    color: #eee !important;
}

.i-color-warm-grey,
.nav__folder-name,
.llc__subject_unread .ll-sj__normal {
    color: white !important;
}

.ll-sp__normal, .ll-sp__search {
    color: #bbb !important;
}

.delimeter, .r-hr {
    border-top-color: #555 !important;
}

.dataset-letters {
    background-color: rgba(0, 0, 0, 0) !important; 
}

.x-ph .w-x-ph {
    background-color: rgba(30, 30, 30, 1) !important;
}

.x-ph__menu__button, .x-ph__menu__button:link, .x-ph__menu__button:visited, .x-ph__menu__button:hover, .x-ph__menu__button:active, .x-ph__menu__button:focus {
    border-left-color: rgba(30, 30, 30, 1) !important;
    border-right-color: rgba(30, 30, 30, 1) !important;
}

.x-ph__link, a.x-ph__link {
    color: white !important;
}

.theme-t2024 .search-panel__wrapper,
.theme-t2024 .portal-menu {
    background-color: rgba(0, 0, 60, 0.8) !important;
}

.r-search-panel,
.search-panel__wrapper .search-panel-button {
    background-color: var(--darkGreyColor) !important;
}

.search-panel__wrapper .search-panel-button__icon {
    background-color: var(--darkGreyColor) !important;
}

.search-panel__wrapper .search-panel-button__icon:hover {
    background-color: #ff9e00 !important;
}

.r-search-panel input {
    color: white !important;
}


/* NEW SERVICE - TASKS */
.c0111 {
    background-color: #222;
}

.c0131 {
    color: white;
}

.list {
    background-color:  #222 !important; 
    color: white !important;
}

.list-item__ico:not(.list-item__ico_flag):not(.list-item__ico_unread) {
    filter:invert(1);
}

.list__hr {
    background-color: #555 !important;   
}




/* MAIN MAIL.RU PAGE */

.grid {
    background-color: #001F50;
    background-image: url(https://i.ibb.co/8gyFp09/clouds.jpg) !important;   
    background-repeat: no-repeat !important;
    background-size: cover !important;
    height: 100%;
    position: absolute;
}


.grid_x-xl .grid__container,
.grid_x-lg .grid__container,
.grid_x-md .grid__container,
.grid_x-sm .grid__container,
.grid_x-xs .grid__container,
.grid_x-xl .is-sticky .search__sticky-container, 
.is-sticky.grid_x-xl .search__sticky-container
{
    background-color: var(--generalBlueBackgroundColor) !important;
}

.grid_x-xl .is-sticky .search__sticky-container, 
.is-sticky.grid_x-xl .search__sticky-container,
.search__sample,
.i-no-right-radius {
    color: white;
}

.tabs-content__item,
.grid__main-col,
.tabs,
.grid__ccol {
    background-color: transparent !important;
}


.grid-middle {
    min-height: 600px;
}

@media (min-height: 850px) {
.grid-middle {
    min-height: 800px;
}
}


.search__background,
.search__sample,
.tabs__dropdown,
.tabs__dropdown-nose:after,
.mailbox__dropdown__container,
.mailbox__addbox__container,
.btn_white,
.footer__dropdown,
.footer__dropdown-nose:after {
    background-color: var(--darkGreyColor) !important;
}

.i-fade-white:after {
    background: linear-gradient(to right,rgba(255,255,255,0) 0,var(--darkGreyColor) 60%);
}

.mailbox {
    background-color: var(--brightBlueBackgroundColor) !important;
}

.mailbox__dropdown__button-account .mailbox__account-email:after, .mailbox__dropdown__button-account .mailbox__account-name:after {
    background: linear-gradient(to right,rgba(255,255,255,0) var(--brightBlueBackgroundColor), var(--brightBlueBackgroundColor) 60%) !important;
}

.mailbox__dropdown__menu_media_item:hover {
    background-color: #222 !important;
}

.mailbox__dropdown__menu_media_item:hover .mailbox__account-email:after, .mailbox__dropdown__menu_media_item:hover .mailbox__account-name_dropdown:after {
    background-image: linear-gradient(to right, rgba(235, 235, 235, 0) 0px, transparent 60%);
}

.mailbox__icon_back, .mailbox__icon_calendar, .mailbox__icon_cloud {
    color: #005ff9 !important;
}



@keyframes highlight {
  60% {
    background-color: var(--brightBlueBackgroundColor);
  }
  100% {
    background-color: transparent;
  }
}

.news-item:after, 
.news-main:after {
    box-shadow: inset -8px 0 16px transparent !important;
}

.tabs__dropdown-button,
.horo-informer__title,
.i-color-black,
.mailbox__account-name,
.news-main__desc {
    color: white !important;
}

.news-item__label.svelte-1tc42cf {
    color: white !important;
}

.rate__icon {
    border-color: black !important;
    filter:invert(1);
}

.x-ph__menu__button .x-ph__menu__button__text,
.grid a {
    color: var(--linkColor) !important;
}

.x-ph__menu__button:hover .x-ph__menu__button__text {
    color: #ff9e00 !important;
}

.news-item a:visited {
    color: var(--visitedLinkColor) !important;
}

.m-bordered.m-animated-shadow.m-animated-borderless.notify.i-link-no-deco.i-absolute {
    background-color: transparent !important;
}

.m-animated-border:before, .m-animated-border:hover:before, .m-animated-borderless:before, .m-bordered:before {
    border-color: transparent;
}

.notify__short-link {
    color: white;
}
}
