style.css:
/* i really want this to be global */
body {
    background: #181818;
    color: #bebebe;
}

/* i really want this to be global */
blockquote {
    background-color: rgba(255, 229, 100, 0.12) !important;
    color: white;
}

/* i really want this to be global */
pre,
code {
    background: #141414 !important;
    color: #ffffffe0 !important;
}

/* i really want this to be global */
.navListItem.navListItemActive:before {
    color: #bebebe;
}

/* i really want this to be global */
.button {
    border-color: #bebebe !important;
    color: #bebebe !important;
}

/* i really want this to be global */
a.hover code,
.onPageNav .toc-headings > li > a.active,
.onPageNav .toc-headings > li > a.hover {
    color: #f30101 !important;
}

.toc .toggleNav .navGroup .navGroupCategoryTitle,
.toc .toggleNav .navGroup .navGroupSubcategoryTitle {
    color: #d6d5d5 !important;
}

.toc .toggleNav ul li a:focus,
.toc .toggleNav ul li a:hover {
    color: #ff5a5a !important;
}

.onPageNav a {
    color: #d6d5d5 !important;
}

div.nav-tabs {
    background: none !important;
}

.tabs .nav-tabs > div.active {
    background: #f0f0f01c !important;
}

/* i really want this to be global */
a {
    color: #6b9df9 !important;
}
