style.css:
@-moz-document domain("tiermaker.com")
{

body
{
	background-color: black;
	color: white;
}

#breadcrumbs
{
	color: white;
}

h1,p,h2,h3,h4,h5,li,ul
{
	color: white;
}

a
{
	color: #00aef3;
}

#footer a 
{
	color: #00aef3;
}

#breadcrumbs a
{
	color: #00aef3 !important;
}

#overlay #modalWrapper #modal
{
	background-color: #272727 !important;
}

.button-link
{
	color: white !important;
}

#tablewrap
{
	border: solid 1px white;
}

#char
{
	background-color: #1a1a1a;
}

#char-tier-outer-container-scroll
{
	background-color: black !important;
}

#char-tier-container-scroll
{
	background-color: black !important;
}

#screenshotShow
{
	background-color: #272727 !important;
	color: white;
}

form div
{
	color: white;
}

#downloadLink
{
	color: black !important;
}

#close
{
	background-color: red !important;
}

::-webkit-scrollbar
{
  background: #0fa8e7;
  height: 15px;
}

::-webkit-scrollbar-track
{
  background: rgba(0, 0, 0, 0.1);
}

::-webkit-scrollbar-thumb
{
  background: rgba(0, 0, 0, 0.5);
}

#copyright a
{
	color: #00aef3;
}

#copyright 
{
	color: #d9f300;
}

::-webkit-scrollbar-corner
{
	  background: black;
}

::-webkit-scrollbar:vertical
{
  background: black;
}

::-webkit-scrollbar-thumb:vertical
{
  background: white;
}

#colorselect span, #backgroundcolorselect span 
{
    border: 2px solid #272727 !important;
}

#colorselect span.selected, #backgroundcolorselect span.selected 
{
    border: solid 2px #fff !important;
}

#copyright::after
{
	content:" | Â© 2020 Dark Theme By @ShadowIX_";
}

}
