style.css:
@-moz-document domain("neal.fun") {
:root{
    scrollbar-color: #2ecc71 #1c1c1c;
}
html,body{
    background: #1c1c1c !important;
    color: #2ecc71;
}
*{
    box-shadow: none !important;
}
a,h1{
    color: #2ecc71 !important;
}
select,input[type="text"],input[type="number"]{
    background: #1c1c1c !important;
    border: 1px solid #2ecc71 !important;
    color: #fff !important
}
.dRSSRR{
    background: #1c1c1c;
    border-bottom: 1px solid #2ecc71;
}
.ejNPMW,.hrBwDi,.cICJXM,.cCxQDi,.fMgaEO a,.hOvbLp,.bZVFjU,.kKVjEw,.gmhThs,.gEVkeR{
    color: #2ecc71 !important;
}
.hGCfmu .category-on,.hGCfmu .inner-category:hover{
    background: #2ecc71 !important;
    color: #1c1c1c !important;
}
.icCGR,.YojKL,.dSjpsN,.fMTTXX,.EXlcp,.JFhnN,.dUQdhi .quick-fact,.dUQdhi .select-box,.TtbvK,.bmdVZl,.hjuUds,.donBae,.hGCfmu .title,.hGCfmu .island-intro,.hGCfmu .island-bottom-bar,.hGCfmu .inner-category,.hdcrPY,.fwBQUl,.bBLKCU .header{
    background: #1c1c1c;
    border: 1px solid #2ecc71;
}
.ibxCBP{
    background: #1c1c1c;
    border-top: 1px solid #2ecc71;
}
.duxtME,.cmLPO{
    background: #1c1c1c;
    color: #fff;
}
.gzzAOu,.fQtXLj,.dUQdhi .article{
    background: #1c1c1c;
    border: 1px solid #2ecc71;
    color: #2ecc71 !important;
}
.hRaFKn:hover .gzzAOu{
    background: #2ecc71;
    color: #1c1c1c !important;
}
.fUKkKd,.kTOsYn,.gUqNft{
    background: #1c1c1c;
}
.hGCfmu .instructions{
    background: #1c1c1c;
    border: 1px solid #2ecc71;
    border-bottom: 0;
}
.jFskBt,.hGCfmu .tool-bar{
    background: #1c1c1c;
    border: 1px solid #2ecc71;
    border-top: 0;
}
.jPOQJF{
    border: 2px solid #2ecc71;
}
.jwPkuL,.emqeyJ,.kKVjEw img{
    filter: invert(65%) sepia(53%) saturate(654%) hue-rotate(91deg) brightness(95%) contrast(77%);
}
.recharts-xAxis.xAxis{
    stroke: #2ecc71 !important;
}
.recharts-text.recharts-cartesian-axis-tick-value{
    fill: #2ecc71 !important;
}
.recharts-default-tooltip,.bBLKCU .article,.deJCkU{
    background: #1c1c1c !important;
    border: 1px solid #2ecc71 !important;
    color: #2ecc71 !important;
}
.cXsITx tr:nth-child(2n+1){
    background: #2ecc71;
}
.cXsITx tr:nth-child(2n+1) td{
    color: #1c1c1c
}
.gpyMAf:after{
    content: " | Dark theme by Maciejozaman";
}
}
