style.css:
@-moz-document url-prefix("https://www.roblox.com/games") {
@keyframes rainbow {
	0% { background-color: #0ff;border-color: #0ff;}
    32.5% { background-color: #0ff;border-color: #0ff;}
	45% { background-color: #0f8;border-color: #0f8;}
    67.5% { background-color: #0ff;border-color: #0ff;}
    100% { background-color: #0ff;border-color: #0ff;}
}

a.btn-common-play-game-lg {
    animation: rainbow 9.5s infinite;
}
}
