@-moz-document domain("acfun.cn"), regexp("^https?://(www\\.acfun\\.cn)."), regexp("^https?://(www\\.acfun\\.cn)/+v+.*") {
/* libs

 */
@-webkit-keyframes fadeInDown {
    0% {
        opacity: 0;
        -webkit-transform: translate3d(0, -100%, 0);
        transform: translate3d(0, -100%, 0)
    }
    to {
        opacity: 1;
        -webkit-transform: translateZ(0);
        transform: translateZ(0)
    }
}
@keyframes fadeInDown {
    0% {
        opacity: 0;
        -webkit-transform: translate3d(0, -100%, 0);
        transform: translate3d(0, -100%, 0)
    }
    to {
        opacity: 1;
        -webkit-transform: translateZ(0);
        transform: translateZ(0)
    }
}

@keyframes breathe {
    from {
        box-shadow: inset 0 0 2px rgba(255, 255, 255, 0);
    }
    to {
        box-shadow: inset 0 0 10px rgba(0, 0, 0, .2);
    }
}
@keyframes fade-in {
    0% {
        opacity: 0;
    }
    /*初始状态 透明度为0*/
    40% {
        opacity: 0;
    }
    /*过渡状态 透明度为0*/
    100% {
        opacity: 1;
    }
    /*结束状态 透明度为1*/
}
@-webkit-keyframes fade-in {
    /*针对webkit内核*/
    0% {
        opacity: 0;
    }
    40% {
        opacity: 0;
    }
    100% {
        opacity: 1;
    }
}

#wrapper {
    animation: fade-in;
    /*动画名称*/
    animation-duration: 0.5s;
    /*动画持续时间*/
    -webkit-animation: fade-in 1.5s;
    /*针对webkit内核*/
}

/* Test */


/* 主页信息中心 */
#header-guide > li.guide-item.guide-user.user-logined > div {
    animation: fade-in;
    animation-duration: 0.2s;
    -webkit-animation: fade-in 0.3s;
    top: 45px;
}
