    -webkit-animation: fade-in .3s;
}
#header-guide > li.guide-item.guide-msg > div {
    opacity: 0.90;
    /*     transform: translateY(-40px); */
    transition-property: opacity;
    transition-duration: 1.5s;
    transition-timing-function: cubic-bezier(0.23, 1, 0.32, 1);
}
#header-guide > li.guide-item.guide-msg > div:hover {
    opacity: 1;
}
.guide-item-con[data-c-w-header] .header-guide .guide-msg .guide-item-con ul {
    box-shadow: 0px 7px 15px 0px rgba(0, 0, 0, .2);
    font-size: 12px;
    background: #fff;
    padding: 0 12px 16px;
    overflow: hidden;
    border-radius: inherit;
}
/* 历史记录 */
[data-c-w-header] .header-guide .guide-item.guide-history .guide-item-con {
    transition: all 300ms;
    width: 350px;
    height: auto;
