style.css:
@-moz-document url-prefix("https://it.amazonforum.com/") {
body
{background: #323232;}

.slds-p-vertical_large, .slds-p-vertical--large
{background: #202125;}

.slds-text-heading_large, .slds-text-heading--large
{color: #f0f0f0;}

.uiTabset--default
{background: #202125;}

html body a, html body a:visited, html body a:active
{color: #909090}

.amazon-forum-custom .forceChatterStyle--default .cuf-feedElement.cuf-feedElement, .amazon-forum-custom .forceChatterFeedItem .comment--threadedCommunity
{background: #727272;
border: 1px solid #323232;}

.slds-text-title
{color: #11111b;}

.forceChatterStyle .comment__footer button, .forceChatterStyle .comment__footer a
{color: #f0f0f0;}

.forceChatterStyle--default .cuf-element
{background: #909090;}

.amazon-forum-custom .forceChatterFeedItem .feed__item-comments--threadedCommunity
{background: #909090;
border: 1px solid #323232;}
}
