style.css:
@-moz-document domain("www.inoreader.com")
{
/*基本 Base*/
.article_full_contents,
.article_title {
    width: 98% !important;
}

.article_footer,
.article_content,
.article_content img {
    max-width: 100% !important;
}

/*阅读器 Article dialog*/
#article_dialog_wrapper {
    width: 70% !important;
    min-width: 1040px;
}

#article_dialog {
    padding: 90px 0px 0px 0px;
}

#article_dialog .article_footer_placeholder_top {
    height: 80px;
    width: 65% !important;
    min-width: 985px;
}

#article_dialog .article_footer {
    top: 20px;
}

#article_dialog > .article_full_contents {
    width: 94% !important;
    margin: 0 auto;
}

/*反广告 Anti-ads*/
.ad_title,
.ad_size_leaderboard,
#sinner_container,
.sinner,
.ad_footer_remove {
    width: 0px !important;
    height: 0px !important;
    padding: 0px !important;
    margin: 0px !important;
    overflow: hidden !important;
}

#reader_pane {
    padding-right: 0px !important;
}

/*列表视图 List view*/
.article_expanded:not(.article_card_nostripe):not(.article_tile) {
    width: 65% !important;
    min-width: 900px;
    margin: 0 auto !important;
}

.article_expanded:not(.article_card_nostripe):not(.article_tile) .article_content {
    max-width: 94% !important;
    min-width: 625px !important;
}

.article_expanded:not(.article_card_nostripe):not(.article_tile) .article_content img {
    max-width: 90% !important;
    min-width: 625px !important;
}

/*扩展视图 Expanded view*/
.article_card {
    width: 75% !important;
}

.article_card .article_content {
    max-width: 80% !important;
    min-width: 625px !important;
}

.article_card .article_content img {
    max-width: 75% !important;
    min-width: 625px !important;
}

/*卡片视图 Card view*/
@media screen and (min-width: 480px) {
    div.article_tile {
        max-width: none;
        width: 220px !important;
        height: 260px !important;
    }

    .article_tile_content_wraper {
        height: 235px;
    }
    .article_tile_content_wraper.no_pic{
        height: 223px;
    }
    
    .article_tile_content_wraper .article_tile_picture {
        height: 220px;
    }
    .article_tile_content_wraper .article_tile_title {
        position: relative;
        padding: 0 10px;
        top: -13px;
        font-size: 11px;
    }
    .article_tile_content_wraper .article_tile_content{
        height:200px;
    }
}

@media screen and (min-width: 1600px) {
    div.article_tile {
        max-width: none;
        width: 400px !important;
        height: 400px !important;
    }
    .article_tile_content_wraper {
        height: 375px;
    }
    .article_tile_content_wraper.no_pic{
        height: 352px;
    }
    .article_tile_content_wraper .article_tile_picture {
        height: 356px;
    }
    .article_tile_content_wraper .article_tile_title {
        position: relative;
        padding: 0 10px;
        top: -12px;
        font-size: 13px;
    }
}

@media screen and (min-width: 1900px) {
    div.article_tile {
        max-width: none;
        width: 600px !important;
        height: 600px !important;
    }
    .article_tile_content_wraper {
        height: 576px;
    }
    .article_tile_content_wraper.no_pic{
        height: 552px;
    }
    .article_tile_content_wraper .article_tile_picture {
        height: 556px;
    }
    
    .article_tile_content_wraper .article_tile_title {
        position: relative;
        padding: 0 10px;
        top: -16px;
        font-size: 16px;
    }
}

.article_tile_footer {
    line-height: 12px;
    min-height: 10px;
    padding: 0 0 5px 8px;
}

.article_tile_footer .icon16 {
    font-size: 12px;
}

.article_tile_footer .popularity_score_span {
    top: 0px;
}
/*杂志视图 Magazine view*/
.article_magazine {
    max-width: 1280px !important;
    height: 320px;
}

.article_magazine_picture {
    width: 350px;
    height: 280px;
}

.article_magazine_content_wraper {
    padding-left: 360px;
}

.article_magazine_title_content {
    min-height: 256px;
}
}
