	/*标题栏强制统一样式*/
	#wrapper #content_left .c-container h3 a, #wrapper #content_left .c-container[tpl=soft] .op-soft-title a, #wrapper #content_left .c-container[tpl=soft] .op-soft-title a em, #wrapper #content_left .result h3 a em, .t.c-gap-bottom-small a, .t a, .op-timeliness-title a, .t.c-group-title a, .op_sp_realtime_group_title a, .c-border a, .c-row a, .video_small_intro a, .t.c-gap-bottom-small a em, .t a em, .op-timeliness-title a em, .t.c-group-title a em, .op_sp_realtime_group_title a em, .c-border a em, .c-row a em, .video_small_intro a em, .c-title-text, .c-title-text em,.news-title_1YtI1 em{
