	}
	
	.soutu-close.c-icon.soutu-close-new {
		position: relative;
		top: -25px;
		left: 533px;
	}
	
	/*⑤百度图片搜索界面*/
	.cms-area-hidden {
		width: 100% !important;
	}
	
	.s_search {
		height: 70px !important;
	}
	
