	background: #EBEBEB;
	color: #000000;
}
.wysiwyg
{
	background: #F5F5F5 !important;
	color: #000000;
	font: 10pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
}

}
