style.css:
@-moz-document domain("werkplekleren.hu.nl") {
:root {
    --backgroundColor: #333;
    --mainTextColor: #CCC;
}

body, #menu-bar, #sidebar-wrapper, .sidebar-nav, .panel-body, .row.well {
    background-color: var(--backgroundColor) !important;
}

#side-bar {
    background-color: var(--backgroundColor) !important;
}

body, h1, h2, h3, h4, p, td {
    color: var(--mainTextColor) !important;
}

.blockTable tbody tr.highlight {
    background: #060 !important;
}

}
