style.css:
@-moz-document url-prefix("https://meet.google.com/") {
div[jscontroller="kAPMuc"] { /* Bottom bar */
    box-shadow: none !important;
    min-width: 0 !important;
    width: auto !important;
    right: 0 !important;
    transform: translateY(0) !important;
    opacity: 0 !important;
	transition: opacity 0.5s !important;
    background-color: transparent !important;
    pointer-events: none !important;
}

div[jscontroller="kAPMuc"]:hover { /* Bottom bar hover */
    opacity: 1 !important;
}

div[jscontroller="kAPMuc"] > div { /* Bottom bar */
    pointer-events: all !important;
}

div[jscontroller="OusaP"], div[jscontroller="NBSUBc"] { /* Bottom bar call buttons */
    margin: 0 !important;}   

div[jscontroller="kAPMuc"] div.jzP6rf { /* Bottom bar first button */
    display: none !important;
}

div.q2u11 { /* Bottom bar call buttons container */
    min-width: 0 !important;
    width: calc(2rem * 2);
}

div.n8i9t, div.XMjwIe, span.NlWrkb { /* Catpions, present and menu buttons */
    min-width: 0 !important;
    height: auto !important;
}

.I98jWb, .YhIwSc, .XFtqNb > i, .o9fq9d, .Hdh4hc:not(.cIGbvc), .bkbMM { /* Texts */
    color: white !important;
    fill: white !important;
}

.A00RE { /* Mute and camera buttons */
    border: 0 !important;
}

.zCbbgf { /* Hang button */
    display: none !important;
}

.mKBhCf { /* Chat sidebar */
    height: calc(100% - 75px) !important;
}
}
