style.css:
@-moz-document domain("forums.thesims.com") {
/* Darkmode for the Sims forums by Seyjin */

/* Couleur principale du texte */
body, .DataTableWrap thead td, .DataTableWrap .CommentDate, .DataTableWrap .CommentDate:hover, .Section-Discussion .CommentMeta a, .Section-Discussion .DiscussionMeta a, .Section-Discussion .Item.rank-blue .Meta, .Section-Discussion .Item.rank-blue .Meta a, .Section-Discussion .Item.rank-blue .Meta a:focus, .Section-Discussion .Item.rank-blue .Meta a:hover, .Section-Discussion .CommentMeta, .Section-Discussion .DiscussionMeta, .Section-Discussion .PageTitle h1, input.DateBox, input.InputBox, input.SmallInput, textarea, .editor-fullpage .TextBoxWrapper .BodyBox, .editor-fullpage .TextBoxWrapper .wysihtml5-sandbox, .nav-me .Username, .info-bar .breadcrumbs, .info-bar .breadcrumbs a, .info-bar .SignInLinks, .info-bar .SignInLinks a, a.Close {
    color: #ccc;
}

/* Aspect de la barre d’info */
.info-bar, .footer {
    background: #1c1b22;
}

.search-bar .Button, .search-bar .Button:hover, .search-bar .Button:focus {
    background-color: #406f08 !important;
}

.Alert {
    background-color: #e10000;
    border: 1px solid #ff3a3a;
    color: #f5f5f5;
}

/* Recolorisation des icones */
.OptionsTitle, .Sprite16, .ReactSprite, button .Sprite, .QuickSearch .Sprite {
    filter: invert(80%);
}

.HomeCrumb a {
    filter: invert(20%);
}

/* Aspect de la barre de navigation */
.nav-bar {
    background: linear-gradient(rgba(0,0,0,0) 25%,rgba(0,0,0,.1));
    background-color: #222029;
}

.nav-bar li a {
    text-shadow: 0 2px 0 #3b4460;
}

/* Aspect des boutons */
.Button, .Button:focus, body:not(.dataDriven) .Button.Active, body:not(.dataDriven) .ButtonGroup.Open .Button.Handle, body:not(.dataDriven) .Button:focus, body:not(.dataDriven) .Button:active {
    background: linear-gradient(#2096c8,#275d8c);
    border: 1px solid #1e486c;
    color: #f5f5f5;
}

/* Couleur principale du fond de la page */
#Body, div.Popup .Body {
    background-color: #222029;
}

/* Couleur alternative du fond de la page */
.FormWrapper, .editor-fullpage #editor-fullpage-candidate, .editor-fullpage #editor-fullpage-candidate .editor, div.Spoiler.SpoilerConfigured, .Section-Discussion .Message::before {

    background-color: #1c1b22;
}

/* Couleur des trais du tableau */
.DataTable td, .ChildCategories, .Section-Discussion .Item-BodyWrap, .Section-Discussion .DataList .Item, .Section-Discussion .ItemDiscussion .Discussion, .Section-Discussion .DataList.Comments, .MenuItems hr, .footer-logos, .Section-Discussion .UserQuote {
    border-color: #18171e;
}

/* Marques lu/non-lu des messages */
.Item.Read {
    background: linear-gradient(to right,  #1979a2 4px, #fff0 3px,#0001);
}

.Item.New, .Item.Unread {
    background: linear-gradient(to right,  #ff0 4px, #fff0 3px,#0001);
}

/* Couleur des étiquettes */
.Tag {
    background-color: #18171e;
}

/* En-têtes spéciales des messages */
.Section-Discussion .Rank-Admin .Message::before {
    background-color: #406f08;
}

.Section-Discussion .Item.rank-blue .Message::before {
    background-color: #0b5777;
}

/* Indicateur du numéro de la page */
.Pager .Highlight {
    background-color: #1c1b22;
    border-radius: 10px;
    color: #ccc;
}

/* Couleur du texte indiquant qu’un message a été modifié */
div.PostEdited {
    color: #ff3a3a;
}

/* Spoiler spoiler spoiler */
div.Spoiler div.SpoilerTitle input {
    background-color: #222029;
    border-color: #18171e;
    color: #25a8e0;
}

/*  Signaler un abus / Citer / Intéressant / Génial ! / LOL  */
.Reactions .ReactButton:hover {
    opacity: 1;
}

/* Aspect des boites d’édition de textes */
.InputBox, input:focus, textarea:focus, .Activities a.CommentLink, .Activities a.CommentLink:hover {
    background: #222029 !important;
    border-color: #18171e;
}

:focus {
    outline-style: none;
}

/* Aspect des menus */
.MenuItems, .FlyoutMenu {
    border-color: #000;
    background: #222029;
    color: #ccc;
}

.Flyout::before, .Flyout::after {
    border-bottom: 7px solid #000;
    top: -7px;
}

.MenuItems a, .MenuItems a:link, .MenuItems a:visited, .MenuItems a:active {
    color: #ccc !important;
}

.Dropdown.MenuItems a:hover, .Flyout.MenuItems a:hover, .editor-dd-emoji + .editor-insert-dialog .emoji-wrap:hover {
    background-color: #18171e88 !important;
    color: #ccc !important;
}
}
