.CeldaCalificacionLeft, .CeldaCalificacion, .CeldaGeneral, .CeldaHorarioVacia{ /* Celdas calf // Celdas Datos Encuadre // Celdas horario Vacías */
	border-color: #202124;
    border-right: 1px solid #202124;
    border-bottom: 1px solid #202124;
    color: #FFF;
    background-color: #353535;
}
/* Celdas gris claro */
.CeldaBajas{
    border-color: #202124;
    border-right: 1px solid #202124;
    border-bottom: 1px solid #202124;
    color: #FFF;
	background-color: #5C5C5C;
}
/* Celdas gris mas claro */
.CeldaNotificaciones{
    border-color: #202124;
    border-right: 1px solid #202124;
    border-bottom: 1px solid #202124;
    color: #FFF;
	background-color: #E3E3E3;
}

}

