style.css:
/* i really want this to be global */
/* Vivaldi - Candy stripes border for selected tabs */
.tab-position .tab .tab-selection line,
.tab-position .tab .tab-selection rect
{
  stroke-width: 6 !important;
}
.tab-position .tab .tab-selection line.inner,
.tab-position .tab .tab-selection rect.inner
{
  stroke: GhostWhite !important;
}
.tab-position .tab .tab-selection line.outer,
.tab-position .tab .tab-selection rect.outer
{
  stroke-dasharray: 4, 16 !important;
  stroke-dashoffset: 0 !important;
  stroke: DeepPink !important;
}
