style.css:
@-moz-document url-prefix("https://tower.nf/") {
body {
    color: rgba(255, 255, 255, 0.87);
    background-color: #212121;
}

#sidebar {
    background-color: #212121;
    border-right-color: #2e2e2ec9;
}
.card {
    background-color: #2E2E2E;
}
.card .card {
    background-color: rgba(255, 255, 255, 0.05);
}

#sidebar .card {
    background-color: #2E2E2E;
    border-right-color: #2e2e2ec9 !important;
    border-top-color: #2e2e2ec9 !important;
    border-bottom-color: #2e2e2ec9 !important;
}

#sidebar .card .card-body:hover {
    background-color: #3e3e3e !important;
}
.navbar {
    background-color: #303030 !important;
}
.navbar .nav-item .nav-link {
    color: rgba(255, 255, 255, 0.6);
}
#main-tabs .nav-link {
    color: rgba(255, 255, 255, 0.6);
}
#main-tabs {
    border-bottom-color: #2f2e2e
}
#main-tabs .nav-link.active {
    background-color: rgba(255, 255, 255, 0.5);
    border-color: #2f2e2e;
    color: #212121;
}
table.dataTable.display tbody tr.odd,
table.dataTable.display tbody tr.odd > .sorting_1 {
    background-color: rgba(0, 0, 0, 0.25);
    border-color: #2e2e2ec9;
}
table.dataTable.display tbody tr.even,
table.dataTable.display tbody tr.even > .sorting_1 {
    background-color: rgba(0, 0, 0, 0.075);
    border-color: #2e2e2ec9;
}
table.dataTable.cell-border tbody td {
    border-color: #2e2e2ec9;
}
#tasks-table-wrapper .dataTables_filter input,
#search {
    background-color: rgba(0, 0, 0, 0.05);
    border-color: rgba(255, 255, 255, 0.15);
}
.card svg {
    background-color: rgba(0, 0, 0, 0);
}
#metrics-body .plot-container svg {
    background-color: rgba(0, 0, 0, 0.075);
}
#metrics-body .nav-link.active {
    background-color: #dadada;
    border-color: #dadada;
}
#metrics-body .nav-tabs {
    border-bottom-color: #dadada;
}
#metrics-body .nav-link:hover {
    border-color: rgba(0, 0, 0, 0);
}
svg {
    background-color: rgba(255, 255, 255, 0.05);
}
a,
a:hover {
    color: #3498db;
}
footer {
    background-color: #212121;
}
footer .border-top.border-gray-light.text-muted {
    border-top-color: rgba(255, 255, 255, 0.25) !important;
}
}
