style.css:
/* i really want this to be global */
body{background-color:#000000;}
    a,a:visited,#modal,h1,p,.subtitle,.body,#xf,.post_no,i,.imp,.det,.sel-arc,select,input,#text,#alert_message,h3,strong{color:#fff;}
.boardlist a{border-top:3px solid #aed581;}
hr,.clear,.mcd{background-color:#424242;border-color:#424242;}
.exp,.exp::before,.reply,.img,.a,#alert_div,#modal{background-color:#424242;}
.ca li>a .det, .la li>a{background:#424242;}
.ca li,.det{background:#424242;}
.titulo{color:#b61827;}
.reply{border:#424242;border-radius:10px;}
.post-image{border-radius:10px;}
.blotter,.enlinea,.threads .thread,.thread-image{background:#6b9b37;border-color:#6b9b37;color:#fff;border-radius:10px;}
.bottom,.col,.uns lb{background-color:#000000;}
.post.reply.highlighted {
    background:#524c00;
    border-color:#524c00;}
.quote{color:#8bc34a;}
#rr{ background:#616161;}
textarea{color:#fff;}
#updater a, .image_id>a, .lfl, .nuevas_resp, .subtitle>p a, .volarr a, a#mqr, span.omitted a{background:#6b9b37;}
.reply,.intro,.board_image{border-radius:10px;}
.intro time,span.heading{color:#f05545;}
.omitted,.unimportant,li,.av,label,#seg{color:#aeaeae}
.hxs{background:transparent;}
form table tr th {background:#6b9b37;border:1px solid #387002;color:#fff;}
#hnd,.nombre,.cerrar{background:#6b9b37;color:#fff;}
.wbg { background:transparent;}
nav{background: linear-gradient(135deg,rgba(66, 66, 66,1) 0,rgba(66, 66, 66,.9) 20%,rgba(66, 66, 66,1) 100%);
    background: -moz-linear-gradient(-45deg,rgba(66, 66, 66,1) 0,rgba(66, 66, 66,.9) 20%,rgba(66, 66, 66,1) 100%);
    background: -webkit-linear-gradient(-45deg,rgba(66, 66, 66,1) 0,rgba(66, 66, 66,.9) 20%,rgba(66, 66, 66,1) 100%);
    }
.fa-quote-right{color:#6b9b37;}
.post.reply{background-color:#424242;}
.intro {background-color:transparent;}
.sel-arc, select {background-color:#6b9b37;}


:after,:before {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box
}

@media(max-width: 767px) {
.post.reply .intro {
    background-color:#387002;
}
.intro{border-radius:0;}
.post-image{border-radius:10px;}
}
