/* ==UserStyle==
@name        Canvas Dark Mode
@namespace   soupyzinc
@version     1.0.1
@homepageURL https://github.com/SoupyzInc/CanvasDarkMode
@supportURL  https://github.com/SoupyzInc/CanvasDarkMode/issues
@updateURL   https://raw.githubusercontent.com/SoupyzInc/CanvasDarkMode/main/CanvasDarkMode.user.css
@license     MIT
@author      Matthew Okashita
==/UserStyle== */
/*v1.0.1 | Last Updated: 29 October 2020 | 11:40 PM EST*/
@-moz-document domain("instructure.com") {
    :root {
        --dark-grey: #1d1e21 !important;
        --grey: #23272A !important;
        --light-grey: #2C2F33 !important;
        --white-grey: #99AAB5 !important;
        --text: #d7d7d7!important;
    }

    /*DASHBOARD=====================================================*/
    /*=======SITE BACKGROUND COLOR=======*/
    body {
        background: var(--grey) !important;
        color: var(--text) !important;
    }
    
    .eHiXd_bPzo.eHiXd_ycrn, a.eHiXd_bPzo.eHiXd_ycrn, button.eHiXd_bPzo.eHiXd_ycrn, button.eHiXd_bPzo[type="button"].eHiXd_ycrn, button.eHiXd_bPzo[type="reset"].eHiXd_ycrn, button.eHiXd_bPzo[type="submit"].eHiXd_ycrn {
        height: var(--eHiXd-mediumHeight);
        width: var(--eHiXd-mediumHeight);
        color: var(--text) !important;
    }

    /*=======DASHBOARD TEXT BACKGROUND=======*/
    .ic-Dashboard-header__layout {
        border-bottom: 1px solid var(--text) !important;
        background: var(--grey) !important;
    }

    /*=======SIDE BOARD PANEL=======*/
    /*Side Board Panel Background*/
    .ic-app-header {
        background-color: var(--dark-grey) !important;
    }

    /*Selected Side Board Panel Background*/
    .ic-app-header__menu-list-item.ic-app-header__menu-list-item--active .ic-app-header__menu-list-link {
        background: var(--light-grey) !important;
    }

    /*Courses Sub Tab Background*/
    .MYpOQ_bGBk {
        background: var(--grey) !important;
    }

    /*Profile Sub Tab Background*/
    [dir="ltr"] .fLzZc_bxia {
        background: var(--grey) !important;
    }

    /*=======CLASS CARD BACKGROUNDS=======*/
    /*Card Text Background*/
    .ic-DashboardCard__header_content {
        background: var(--light-grey) !important;
    }

    /*Bottom Card Tray*/
    .ic-DashboardCard {
        box-shadow: 0 2px 5px rgba(0, 0, 0, 0.3);
        background: var(--light-grey) !important;
    }

    /*=======ACCOUNT MENU BACKGROUND=======*/
    Style Attribute {
        --fLzZc-smallWidth: 28em;
        background: var(--light-grey) !important;
    }

    element.style {
        --fLzZc-smallWidth: 28em;
        background: var(--light-grey) !important;
    }

    /*Recent Feedback Sub Text*/
    #right-side .right-side-list li em,
    #right-side .right-side-list li p {
        color: var(--text) !important;
    }

    /*COURSES=======================================================*/
    /*Most Text*/
    a {
        color: var(--text) !important;
        /*text-decoration: none !important;*/
    }

    /*Course Name Background*/
    #breadcrumbs > ul > li {
        background: var(--grey) !important;
    }

    /*Header Bar Background*/
    .header-bar {
        border-bottom: 1px solid #99AAB5 !important;
        background-color: var(--grey) !important;
    }

    /*=======HOME=======*/
    /*Home Page | Course Name Color*/
    .pages.show .course-title {
        color: var(--text) !important;
    }

    /*Home Page | To Do List Icon & Subtext Colors*/
    .dxCCp_bGBk {
        color: var(--text) !important;
    }

    /*=======MODULES=======*/
    /*Modules Page | Expand/Collaps Button*/
    .btn,
    .Button,
    .ui-button {
        background: #FFFFFF00 !important;
        color: var(--text) !important;
        border-color: #99AAB5 !important;
    }

    /*Modules Page | Modules Background*/
    #context_modules {
        background: var(--grey) !important;
    }

    /*Modules Page | Module Name Bar*/
    .item-group-condensed .ig-header {
        background: var(--light-grey) !important;
        border: 1px solid #99AAB5 !important;
        color: var(--text) !important;
        /*Drop Down Arrow & Subtext*/
    }

    /*Modules Page | Item In Module*/
    .item-group-condensed .ig-row {
        background: var(--grey) !important;
        border-bottom: 1px solid #99AAB5 !important;
    }

    /*Modules Page | Item Icon*/
    .ig-list .ig-row {
        color: var(--text) !important;
    }

    /*Modules Page | Footer Bar Background*/
    .item-group-container {
        background: var(--grey) !important;
    }

    /*Modules Page | Modules Group Title Text*/
    .ig-header .name {
        color: var(--text) !important;
        text-shadow: 0px 0px 0 rgba(255, 255, 255, 0.5) !important;
    }

    /*Assignments Page | Assignments Group Titles*/
    .accessible-toggler {
        outline-width: 0.125rem;
        outline-color: transparent;
        outline-style: solid;
        color: var(--text) !important;
    }

    #right-side .details .header {
        color: var(--text) !important;
        font-weight: bold;
    }

    /*=======ASSIGNMENTS=======*/
    /*Assignnments Page | Submission Box*/
    .ui-tabs .ui-tabs-panel {
        display: block;
        padding: 1em 1.4em;
        background-color: var(--grey) !important;
        border-width: 0 1px 1px 1px;
        border-radius: 0;
    }

    /*Assignments Page | Submission Box Text*/
    .ui-widget-content {
        border: 1px solid var(--text) !important;
        color: var(--text) !important;
    }

    /*Assignments Page | Selected Tab Background*/
    .ui-tabs .ui-tabs-nav li.ui-tabs-active,
    .ui-tabs .ui-tabs-nav li.ui-tabs-active.ui-state-hover,
    .ui-tabs .ui-tabs-nav li.ui-tabs-active:hover {
        border-color: var(--text) !important;
        background: var(--light-grey) !important;
    }

    /*Assignments Page | Unselected Tab Background*/
    .ui-tabs .ui-tabs-nav li {
        background: var(--grey) !important;
        border-color: transparent;
        box-shadow: none;
    }

    /*Assignments Page | Comment Box Background*/
    textarea,
    input[type="text"],
    input[type="password"],
    input[type="datetime"],
    input[type="datetime-local"],
    input[type="date"],
    input[type="month"],
    input[type="time"],
    input[type="week"],
    input[type="number"],
    input[type="email"],
    input[type="url"],
    input[type="search"],
    input[type="tel"],
    input[type="color"],
    .uneditable-input {
        background-color: var(--light-grey) !important;
        border: 1px solid var(--text) !important;
        box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075);
        transition: border linear 0.2s, box-shadow linear 0.2s;
        color: var(--text) !important;
    }

    /*=======GRADES=======*/
    /*Grades Tab | Hover Sub Text*/
    #grades_summary th.title .context {
        color: var(--text) !important;
    }

    /*Grades Tab | Drop Town Title Text*/
    .fCrpb_egrg,
    .fCrpb_egrg.fCrpb_fVUh,
    label.fCrpb_egrg {
        color: var(--text) !important;
    }

    /*=======ANNOUNCEMENTS=======*/
    /*Announcements Tab | Certain Hyperlink Text[Citation Needed]*/
    .eHiXd_brAJ,
    a.eHiXd_brAJ,
    button.eHiXd_brAJ,
    button.eHiXd_brAJ[type="button"],
    button.eHiXd_brAJ[type="reset"],
    button.eHiXd_brAJ[type="submit"] {
        color: var(--text) !important;
    }

    /*Announcements Tab | Announcemnt Post Header & Footer Backgrounds*/
    .toolbarView .headerBar {
        background: var(--grey) !important;
        border: 1px solid var(--text) !important;
    }

    /*Announcements Tab | Announcemnt Reply Header Background*/
    .discussion_entry {
        background: var(--grey) !important;
    }

    /*Announcements Tab | Announcemnt Reply Footer Background*/
    .discussion-entries .entry {
        background: var(--grey) !important;
        border: 1px solid var(--text) !important;
    }

    /*Announcements Tab | Announcemnt Reply Time Text*/
    .discussion-pubdate,
    .discussion-tododate {
        color: var(--text) !important;
    }

    /*Announcements Tab | Reply Text*/
    .cjUyb_bGBk.cjUyb_fpfC,
    input.cjUyb_bGBk[type].cjUyb_fpfC {
        color: var(--text) !important;
    }

    /*Announcements Tab | Reply Box*/
    .discussion-reply-box {
        background-color: var(--light-grey) !important;
        border: 1px solid var(--text) !important;
        color: var(--text) !important;
    }

    /*While Replying | Media Insert Tabs Body (Right Side)*/
    .cDwzl_caGd {
        background: var(--light-grey) !important;
        border-color: var(--text) !important;
        color: var(--text) !important;
        width: 100%;
    }

    /*While Replying | Media Insert Tabs (Right Side)*/
    .dXATK_cAug:not([aria-selected]):not([aria-disabled]):hover, .dXATK_cAug[aria-selected] {
        background: var(--light-grey) !important;
        border-color: var(--text) !important;
    }

    /*While Replying | Media Insert Tabs (Right Side) | Insert Folders*/
    ._1iyfu1y {
        color: var(--text) !important;
    }

    /*While Replying | Media Insert Tabs (Right Side) | Insert Files*/
    ._xl47ao2 {
        color: var(--text) !important;
    }

    /*While Replying | Media Insert Tabs (Right Side) | Insert Links*/
    [dir="ltr"] .eHiXd_eESV, [dir="ltr"] a.eHiXd_eESV, [dir="ltr"] button.eHiXd_eESV, [dir="ltr"] button.eHiXd_eESV[type="button"], [dir="ltr"] button.eHiXd_eESV[type="reset"], [dir="ltr"] button.eHiXd_eESV[type="submit"] {
        background: var(--light-grey) !important;
        color: var(--text) !important;
    }

    /*While Replying | Media Insert Tabs (Right Side) | Insert Links | Drop Down Box Options*/
    [dir="ltr"] .MlJlv_ycrn .MlJlv_dUEG {
        background: var(--light-grey) !important;
    }

    /*Bold Text w/ Shadow | Announcemnts Tab Word Count, Module Group Title Text*/
    .mce-label {
        /*text-shadow: 0 0px 0px rgb(255 255 255 / 75%) !important;*/
        color: var(--text) !important;
    }

    /*=======DISCUSSION=======*/
    /*Open Discussion Wrapper Background*/
    .pinned-discussions-v2__wrapper, .unpinned-discussions-v2__wrapper {
        border: 1px solid var(--text) !important;
        background-color: var(--grey) !important;
    }

    /*Discussion Background*/
    .ic-discussion-row {
        background-color: var(--grey) !important;
    }

    /*Discussion Date Text*/
    .ic-item-row__content-col:first-child {
        color: var(--text) !important;
    }

    /*Closed for Comments*/
    .closed-for-comments-discussions-v2__wrapper {
        border: 1px solid var(--text) !important;
        background-color: var(--grey) !important;
    }

    /*Closed for Comments Text*/
    .cjUyb_bGBk.cjUyb_eQnG, input.cjUyb_bGBk[type].cjUyb_eQnG {
        color: var(--text) !important;
    }

    /*Closed For Comments when Empty*/
    .discussions-v2__container-image {
        background-color: var(--grey) !important;
        border: 0.125rem dashed var(--text) !important;
    }

    /*=======FILES=======*/
    /*Top Bar*/
    .ViewerControls {
        background-color: var(--dark-grey) !important;
        color: var(--text) !important;
    }

    /*Top Bar | Button Background*/
    .AnnotationControlButton {
        background-color: var(--dark-grey) !important;
    }

    /*Alpha Background*/
    .Pages {
        background-color: var(--light-grey) !important;
    }

    /*Footer*/
    .module-sequence-footer .module-sequence-footer-content {
        background: var(--grey) !important;
        border-top: 1px solid var(--text) !important;
    }
}
