	background: #FDFCE4!important;
	border: 1px solid #eec!important;
	font-size: 110%!important;
	padding: 3px 8px!important;
	white-space: pre-wrap!important;
}

/*___ Highlight Background _________________________________________________________*/

.collection-video-playlist .video-list .article.is-active,
.sf-menu li:hover,
selector:hover
{
	background: linear-gradient(to right, #383838, #0000)!important;
	border-left-color: #434343!important;
}
}

@-moz-document domain("foxnews.com"), domain("foxbusiness.com") {
/*__________________________________________________________________________________*/

.collection-market-indexes .index,
.article,
.collection-opinion .article,
.row-fluid .span3,
.row-fluid .span4,
section .box
{
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.collection-market-indexes .index:hover,
.article:hover,
.collection-opinion .article:hover,
.collection-video-playlist .video-list .article.is-active,
.row-fluid .span3:hover,
.row-fluid .span4:hover,
section .box:hover
{
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

.collection-opinion .quote-bubble,
img
{
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img
{
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/

selector
{
	opacity: .8!important;
}

selector:hover
{
	opacity: 1!important;
}
}

@-moz-document domain("foxnews.com"), domain("foxbusiness.com") {
/* Checkmark _______________________________________________________________*/

.collection-ballot-measures .item-table .table-heading .value::after
{
	top: -8px;
}

/* Checked ________________________*/

.collection-ballot-measures .item-table.winner-yes .table-heading .value::after
{
	background: url(https://i.ibb.co/HgsTqFS/checkbox-checked.png) no-repeat!important;
	box-sizing: border-box;
}

/* Unchecked ________________________*/

Select
{
	background: url(https://i.ibb.co/fM9ptv1/checkbox-empty.png) no-repeat!important;
	box-sizing: border-box;
}

.collection-ballot-measures .item-table.winner-yes .table-heading .value::after
{
	background-size: 36px!important;
	height: 39px!important;
	padding-left: 31px!important;
	width: 1px!important;
}


/* Search _______________________________________________________________*/

.collection-market-indexes .input-search,
.sf-sub-indicator,
.site-header .meta .search-toggle a,
.td-menu-header .td-sp,
.toolbars.desktop-nav .user-search .search .search-wrap a
{
	background: url(https://image.ibb.co/hVxuzR/search.png) no-repeat!important;
	box-sizing: border-box;
}

.collection-market-indexes .input-search,
.sf-sub-indicator,
.site-header .meta .search-toggle a,
.td-menu-header .td-sp,
.toolbars.desktop-nav .user-search .search .search-wrap a
{
	background-size: 21px!important;
	height: 26px!important;
	padding-left: 26px!important;
	width: 10px!important;
}

.collection-market-indexes .input-search
{
	padding-left: 11px!important;
}


}
