style.css:
input[type=checkbox] {
    margin-right: 5px;
    cursor: pointer;
    font-size: 14px;
    width: 15px;
    height: 12px;
    position: relative;
}
input[type=checkbox]:after {
    position: absolute;
    width: 10px;
    height: 15px;
    top: 0;
    content:" ";
    background-color: #b1b1b1;
    color: #fff;
    display: inline-block;
    visibility: visible;
    padding: 0px 3px;
    border-radius: 3px;
}
input[type=checkbox]:checked:after {
    content:"âœ“";
    font-size: 13px;
    background-color: #0275fe;
    font-weight: 500;
    line-height: 15px;
}
input[type=checkbox]:indeterminate:after {
    line-height: 12px;
    content:"-";
    font-size: 22px;
    font-weight: 500;
}
