style.css:
@-moz-document url-prefix("https://www.py4e.com/tools/pythonauto/") {
/* Backgrounds */
body{ background-color: #111 !important;}
pre#output.inputarea, pre#desired.inputarea, .CodeMirror-scroll, .CodeMirror-gutter, div.well pre{ background-color: #202020 !important;}
.CodeMirror-lines{ background-color: #fff !important;}


/* Text colors */
div#left.top_panel b, div#right.bottom_panel b, div#footer, pre#output.inputarea, pre#desired.inputarea, div.well pre{ color: #fff !important;}


/* Others */
.CodeMirror-gutters{ border: none !important;}
}
