style.css:
@-moz-document url-prefix("https://bitcoin.fr") {
/* Backgrounds */
.td-main-content-wrap.td-container-wrap, article.post{ background-color: #202020 !important;}


/* Text colors */
h1 > a, h2 > a, h3 > a, h4 > a, h5 > a, h6 > a, .widget li a, .tdi_2_c2c a, .tdi_3_5cc a, .tdi_3_5cc p, .tdi_4_b02 a, .tdi_7_d50 a, .tdi_8_d7e a, .tdi_8_d7e p, .tdi_9_e3e a,
.entry-title, article .td-post-content.tagdiv-type p{ color: #fff !important;}
.td-post-author-name a, .widget li, header .td-post-author-name, header .td-post-date, .td-post-template-1, header .td-post-comments a{ color: #AAA !important;}


/* Other */
.tdc-header-wrap .td-container-header.td-header-row.td-header-header{ display: none;}
}
