style.css:
@-moz-document domain("crabdance.pp.ua") {
html {
	height: 100%;
}
body {
	background: url(https://creative-moscow.ru/assets/images/background.jpg) no-repeat #1d1e22;
	background-size: cover;
	background-attachment: fixed;
}
.discord-btn, .headerbar, #page-footer, #phpbb_announcement > div > div > strong, #ajaxchat .forabg {
	display: none;
}
.chat-message {
	font-size: 15px;
}
#phpbb_announcement > div > div:after {
	color: red;
	content:'Кира питуч ко ко ко 🐓';
	font-size: 20px;
}
}
