style.css:
@-moz-document url-prefix("https://app.idorecall.com/recalls") {
/* Recalls */
.meta-container, .recalls-list-wrapper, .tags-overlay-container, .recall-item:hover{ background-color: #202020 !important;}
.recall-item .recall-title{ color: #fff !important;}
.filter-btn, .tags-overlay-container, .search-criteria{ color: #76808F !important;}
}

@-moz-document url-prefix("https://app.idorecall.com/library") {
/* Library */
.meta-container, .library-list-wrapper, .tags-overlay-container, .tag:hover{ background-color: #202020 !important;}
.filter-btn, .tags-overlay-container, .search-criteria{ color: #76808F !important;}
.library-title-container .title, .create-note, .meta-container-bottom{ color: #fff !important;}
}

@-moz-document url-prefix("https://app.idorecall.com/practice") {
/* Practice */
html, body, .practice-footer{ background-color: #202020 !important;}
.achieve-item-wrap, .progress-bar-determinare{ color: #fff !important;}
}
