.myml-nav__section-title {color: #ccc;}
.myml-nav__section-subtitle {color: #737373;}
.myml-nav__section--active .myml-nav__section-icon path, .myml-nav__section--has-active-item .myml-nav__section-icon path {fill: #3483fa;}
.myml-nav a {color: #999;}
.myml-nav .myml-nav__menu-item--active {color: #ccc;}
/*--Seccion-Favoritos--*/
.item__result-container {background: #000;}
.mfControl {border-bottom: 1px solid #212121;}
.ui-button--primary.ui-button--disabled, .ui-button--primary:disabled, .ui-button--primary[disabled] {background-color: #191919;}
.itemList {border-bottom: 1px solid #212121;}
.itemList .itemTitle a {color: #999;}
.itemList .itemTitle a:hover {color: #fff;}
.itemList .price-info .price {color: #ccc;}
.message-info {background-color: #0a0a0a; color: #999;}
.modeless-box .modeless-box-noresults {background-color: #070707;}
.modeless-box-footer {background-color: #000;}
.modeless-box .bookmarks-title {color: #ccc;}
.modeless-box .bookmarks-price {color: #ccc;}
.modeless-box .modeless-box-item-wrapper:hover {background: #0a0a0a;}
