}

@-moz-document domain("mercadolibre.com.mx") {
/*--Multi-opcion-de-compra--*/
.ui-pdp-s-header {background-color: #000;}
.ui-pdp-s-table {background: #000;}
.ui-pdp-table--col-5 > .ui-pdp-table__cell {color: #ddd;}
/*--Recomendaciones--*/
.ui-recommendations-comparator__title {color: #bbb;}
.andes-tab--selected {color: #bbb;}
.andes-tab__link, .andes-tab__link:visited {color: #bbb;}
}
