        --filter-6c727e-mangaBlue: invert(50%) sepia(66%) saturate(1178%) hue-rotate(182deg) brightness(103%) contrast(102%)!important;
        --filter-bdc7cf-mangaBlue: invert(50%) sepia(98%) saturate(1080%) hue-rotate(180deg) brightness(101%) contrast(102%)!important;
    }
    html,
    body {
        font-family: "Microsoft Yahei UI", "Microsoft Yahei", "微软雅黑", "Segoe UI", Tahoma, "宋体b8b\4f53", SimSun, sans-serif!important;
    }
    html,
    body,
    #app {
        background-color: var(--color-21)!important;
        color: var(--color-86)!important;
    }
    a {
        color: var(--color-86)!important;
    }
    a:hover {
        color: var(--bili-blue)!important;
    }
    input:focus {
        outline: none!important
    }
    input::-webkit-input-placeholder,
    textarea::-webkit-input-placeholder {
        color: var(--color-86)!important;
    }
    input:-moz-placeholder,
    textarea:-moz-placeholder {
        color: var(--color-86)!important;
    }
    input::-moz-placeholder,
    textarea::-moz-placeholder {
        color: var(--color-86)!important;
    }
    input:-ms-input-placeholder,
    textarea:-ms-input-placeholder {
        color: var(--color-86)!important;
    }
    input[type="checkbox"] {
        position: relative;
        display: inline-block!important;
        margin-right: 3px;
        width: 13px!important;
        height: 13px!important;
        vertical-align: middle;
        text-align: center;
        line-height: 13px;
    }
    input[type="checkbox"]::before {
        position: absolute;
        top: 0;
        left: 0;
        width: 13px!important;
        height: 13px!important;
        border: 1px solid var(--color-42);
        background: var(--color-2c);
        content: "";
        line-height: 13px;
    }
    input[type="checkbox"]:checked::before {
        position: absolute;
        top: 0;
        left: 0;
        border: 1px solid var(--bili-blue);
        background-color: var(--bili-blue);
        color: var(--color-ff);
        content: "♪";
        text-align: center;
        line-height: 13px;
    }
    /* ===浏览器滚动条=== */
    ::-webkit-scrollbar {
        width: 6px!important;
        height: 6px!important;
    }
    ::-webkit-scrollbar-track-piece {
        border-radius: 0!important;
        background-color: var(--color-14)!important;
    }
    ::-webkit-scrollbar-thumb:vertical {
        height: 5px!important;
        border-radius: 6px!important;
        background-color: var(--bili-blue)!important;
    }
    ::-webkit-scrollbar-thumb:horizontal {
        width: 5px!important;
        border-radius: 6px!important;
        background-color: var(--bili-blue)!important;
    }
    ::-webkit-scrollbar-corner {
        border-radius: 0!important;
        background-color: var(--color-14)!important;
    }
    /* 自定义滚动条 */
    .iScrollIndicator {
        border: none!important;
        background-color: var(--bili-blue)!important;
    }
    /* -- 登录 --  */
    .nav-link .nav-link-ul .nav-link-item .link .navbar_logo {
        display: none!important;
    }
    a[href="//www.bilibili.com"].link.van-popover__reference::before {
        margin-right: 16px!important;
        width: 70px!important;
        height: 32px!important;
        background-image: url(https://www.asifadeaway.com/Stylish/bilibili/navbar_logo.svg)!important;
        content: ""!important;
    }
    .login-panel {
