@-moz-document domain("bilibili.com") {
    /* ===适配 Bilibili Evolved 插件=== */
    /* --> 仅适配操作面板、部分常用设置下插件添加的元素及不会与本样式冲突的元素 */
    /* --> 因为此插件修改样式是通过插入新的页内样式直接全局修改，并没有在被修改样式的元素上添加代表此插件的 id 或 class，因此会与本样式冲突，二者无法兼得。 */
    /* --> 若打开插件中会改变页面样式或布局的设置，会出现样式错乱的现象。 */
    /* --> 此插件自带夜间模式，若需要使用以上提到会与本样式冲突的功能，可以选择使用此插件自带的夜间模式。 */
    /* -- 背景 -- */
    [class*="gui-settings"] *:not(i) {
        background-color: var(--color-21)!important;
    }
    asi {
        background-color: var(--color-2c)!important;
    }
    .gui-settings-checkbox-container .gui-settings-checkbox::after,
    .gui-settings-dropdown ul li:hover,
    .panel-area .bb-comment .fold-comment,
    .gui-settings-header .operations .operations-menu .operation:hover {
        background-color: var(--color-27)!important;
    }
    [class*="gui-settings"] .custom-color-preview {
        background: var(--theme-color)!important;
    }
    .gui-settings-checkbox-container .gui-settings-checkbox {
        background: var(--bili-red-50)!important;
    }
    .gui-settings-checkbox-container input:checked~.gui-settings-checkbox {
        background: var(--bili-lightblue-50)!important
    }
    /* -- 字体 -- */
    [class*="gui-settings"] *,
    .panel-area .bb-comment .fold-comment {
        color: var(--color-86)!important;
    }
    .bb-comment .comment-list .list-item .info .hate span,
    .bb-comment .comment-list .list-item .info .like span {
        color: var(--color-66)!important;
    }
    p span[style*="background:yellow"] {
        color: var(--color-21)!important;
    }
    .gui-settings-dropdown ul li:hover,
    .panel-area .bb-comment .fold-comment:hover,
    .gui-settings-header .operations .operations-menu .operation:hover {
        color: var(--bili-blue)!important;
    }
    /* -- 边框 -- */
    [class*="gui-settings"] * {
        border-color: var(--color-42)!important;
    }
    /* -- 图标 -- */
    [class*="icon-"]::after {
        filter: var(--filter-00-86)!important;
    }
}
