@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("depositfiles.com") {   
div.info, #download_url { margin-top: 50px !important; border: none !important; }  
.chousetype .grayline td { background: none !important; }  
#main_menu, .chousetype strong, [src="http://img3.depositfiles.com/images/speed_small.gif"], 
[src="http://img3.depositfiles.com/images/speed_small_gold.gif"], [value="FREE downloading"], 
div.banner1, div.string, div.report, a.forget, td.text, a[href="javascript:show_gold_offer('');"], 
[src*="http://ads.depositfiles.com/ad.php?"], div[class="gold_speed_promo_block hide_download_started"], 
.boxtitle, table.boxtable, br.separator, div#box_small, div#foobar, td.ctable1right, 
div[class="upload_form upload_form_not_index"], div.support  
{ display: none !important; }   }
