style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("juick.com") {

HTML { BACKGROUND: #000000 !important; }
#content { BACKGROUND: #008000 !important; COLOR: #00ff00 !important; }
A { COLOR: #ffff00 !important; TEXT-DECORATION: none !important; }
strong { border: 1px solid #00ff00 !important; }
H1 { COLOR: #00ff00 !important; }
H2 { COLOR: #ffffff !important; }
#header { BORDER-BOTTOM-COLOR: #00ff00 !important; BACKGROUND: #505040 !important; }
#cse-search-box .inp { BORDER-COLOR: #00ff00 !important; BACKGROUND: #008000 !important; COLOR: #ffff00 !important; }
#cse-search-box .subm { BORDER-COLOR: #00ff00 !important; BACKGROUND: #008000 !important; COLOR: #ffff00 !important; }
#userinfo H1 { COLOR: #00ff00 !important; }
#userinfo .status { COLOR: #ff0000 !important; }
#pagetitle { COLOR: #ffff00 !important; }
#pagetabs li { BACKGROUND: #505040 !important; BORDER-COLOR: #00ff00 !important;}
#content { BORDER-TOP-COLOR: #00ff00 !important; }
#content SMALL { COLOR: #00ff00 !important; }
#content H3 { BORDER-BOTTOM-COLOR: #00ff00 !important; }
SMALL { COLOR: #00ff00 !important; }
#userlinks { BORDER-COLOR: #00ff00 !important; COLOR: #ffff00 !important; }
#userlinks A { COLOR: #00ff00 !important; }
#hlinks A { COLOR: #ffff00 !important; TEXT-DECORATION: underline !important; }
#lcol { BACKGROUND: #000000 !important; }
#lcol P { MARGIN: 0 0.5em 0 0.5em !important; }
#lcol H2, #lcol H2 A { BORDER-BOTTOM-COLOR: #00ff00 !important; TEXT-ALIGN: center !important; COLOR: #ffffff !important; }
#rcol H2 { BORDER-BOTTOM-COLOR: #00ff00 !important; COLOR: #ffffff !important; }
#footer, #footer A { COLOR: #ffff00 !important; }
}
