style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

   .GenericStory, .UIStory {
      border-bottom: 1px solid #999;
      margin-top: 5px;
   }
   .GenericStory_Message, .UIIntentionalStory_Message {
      background: #ECEFF5 !important;
      -moz-border-radius:5px;
      padding: 5px !important;
   }
   .UIIntentionalStory_Names {
      display: block !important;
      direction: ltr;
   }
   .UIIntentionalStory_Content {
      direction: ltr;
      text-align: left;
   }
   .UIStoryAttachment {
      padding-top: 10px;
   }
   .comment_actual_text {
      display: block !important;
   }
   .GenericStory_Name {
      display: block !important;
   }
   .ufi_section {
      background: #ECEFF5 !important;
      -moz-border-radius:5px;
   }

}
