style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iphone-girl.jp") {

#header,
#footer_contents_bg,
.side_apps,
#leftsidebar,
#rightsidebar,
#footer,
.navigation,
.navigation + div,
.navigation + div + div,
.navigation + div + div + div,
.navigation + div + div + div + br,
.navigation + div + div + div + br + script + p,
.trackbackspace,
.pagetop,
a[href="http://twitter.com/iphonejoshi"],
a[href="http://www.iphone-girl.jp/feed/"],
a[href="#iphone_girl"]
{ display: none !important; }

body,
#page,
#content
{
  background: #ffffff !important;
  width: 100% !important;
  overflow: visible !important;
  float: none !important;
  margin: 0 !important;
}

}
