style.css:
@-moz-document domain(twitter.com), domain(www.twitter.com) {

#content .doing td {padding:3px !important}
#content .doing td strong a {display:block;font-size:smaller;color:#ccc !important}
#content .doing td .entry-content {background-color:#eee !important;display:block;padding:3px !important;margin:3px 0 3px -3px !important}
#content .doing td .entry-meta {display:block;width:100%;color:#ccc}
#content .doing td .entry-meta a {color:#ccc !important}
#content .doing td .entry-meta span {display:block;width:100%;text-align:right;position:relative;top:-1.3em}
#content .doing td .entry-meta > img {position:relative;top:-1.3em}
#content .doing td {vertical-align:top !important;border-bottom:none !important}
#content .doing td.thumb {border-right:1px solid #eee;}

}
