style.css:
@-moz-document domain('myshows.ru'), url-prefix('http://myshows.ru/') {
body {background: #CCE7FF; font-family: tahoma; font-size: 12px;}
div[class="header-container"] {background: #CCE7FF}
div[class="lside header-lside"] {background: #CCE7FF}
div[class="no-float"] {background: transparent; font-size: 32px !important;}
div[class="rside header-rside"] {background: #CCE7FF}
div[class="content-container"] {background: #E5F3FF;}
div[class="block"] {background: #E5F3FF;}
h3[class="header"] {background: #E5F3FF; font-size:20px; color: #0061A3 !important;}
div[class="rside content-rside"] {background: #E5F3FF}
div[class="footer-container"] {background: #CCE7FF; color: #0061A3 !important;}
div[class="footer inner"] {background: #CCE7FF; font-size: 10px; color: #0061A3 !important;}
div[class="footer-left"] {background: #CCE7FF; color: #0061A3 !important;}
}
