style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://arduino.cc/forum/')

{
body { background: #222222 !important; color: #666666 !important }

.windowbg, .windowbg2, .windowbg-l, .windowbg2-l, .windowbg3, .titlebg, .titlebg2, .titlebg3, .titlebg-l, .titlebg2-l, .titlebg-r {
background: #444444 !important;
color: #EEEEEE !important;
border-bottom: 1px solid #AAAAAA !important;
border-left: 1px solid #AAAAAA !important;
}

.titlebg-r, .windowbg2 { border-right: 1px solid #AAAAAA !important; }
.titlebg2 { border-top: 1px solid #AAAAAA !important; border-right: 1px solid #AAAAAA !important; }

#navBreadCrumb { color: #EEEEEE !important }

.smalltext a, a:link, a:visited, .boardName, .nav:link, .nav:visited { color: #19a4ba !important }

.titlebg, .titlebg-l, .titlebg-r, tr.titlebg th, tr.titlebg td, .titlebg2, tr.titlebg2 th, tr.titlebg2 td, .catbg3, tr.catbg3 td, tr.catbg3-p td { background: #444444 !important; color: #EEEEEE !important; }

td, th, tr { color: #EEEEEE !important }

#al a, #ssotoolbar #al a.t0, #ar a, #ar a.t0 { color: #19a4ba !important }

.catbg, .catbgf, .catbgf-m, .catbgnew { background-color: #111111 !important }

.bordercolor { background: none repeat scroll 0 0 #666666 !important }

}
