style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dl-video.net") {

/* Non-display part */
#footContents,
#headContents,
#rightContents,
#menuLeftContents,
.mainBodyBottom,
.mainBody2,
.mainBody > div > iframe
{ display: none !important; }

/* Display part */
body,
#baseBlock,
#topTitle,
#contents,
#leftContents,
#mainContents,
.mainBody,
.mainHead
{
    width: auto !important;
    color: #000000 !important;
    background: #ffffff !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
}

#urlform
{
    width: auto !important;
    color: #000000 !important;
    background: #666666 !important;
    border: none !important;
    overflow: visible !important;
    float: none !important;
}

}
